import { useState } from "react";

const videoCourses = [
  { title: "React Basics", url: "https://www.w3schools.com/html/mov_bbb.mp4" },
  { title: "Advanced React", url: "https://www.w3schools.com/html/movie.mp4" },
  { title: "UI/UX Design", url: "https://www.w3schools.com/html/mov_bbb.mp4" }
];

export function Courses() {
  const [selectedVideo, setSelectedVideo] = useState(videoCourses[0]);

  return (
    <div>
      <h2 className="text-3xl font-semibold mb-4">Courses</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {videoCourses.map((course, index) => (
          <button
            key={index}
            onClick={() => setSelectedVideo(course)}
            className="bg-white rounded-xl shadow-md p-4 hover:bg-blue-50"
          >
            {course.title}
          </button>
        ))}
      </div>
      <div className="mt-6">
        <h3 className="text-2xl font-semibold mb-2">Now Playing: {selectedVideo.title}</h3>
        <video className="w-full max-w-3xl rounded-lg shadow-lg" controls>
          <source src={selectedVideo.url} type="video/mp4" />
        </video>
      </div>
    </div>
  );
}
